let toast =ref()
onMounted(() => {
    showtoast.onbind(toast.value)
})

function toRoute(item) {
    console.log(item)
    if (item.url.includes('/pages')) {
        uni.navigateTo({
            url: item.url
        })
    }else{
        takephoto()
    }
}
function takephoto() {
  uni.scanCode({
    onlyFromCamera: false,
    autoZoom: true,
    success: function (res) {
      console.log('条码类型：' + res.scanType);
      console.log('条码内容：' + res.result);
    },
    fail: function (res) {
      showtoast.onError(res.errMsg)
      console.log(res.errMsg)
    }
  })
}
function back() {
    uni.navigateBack({
        delta: 1
    })
}
</script>

